    <script type=""text/javascript"">
        // INSERT
        function saveClass() {
            var name = $(""#className"").val();

            if (name.trim() === """") {
                alert(""Course name required"");
                return;
            }

            $.post(""https://localhost:44382/Master/CoursePost"", { className: name }, function (res) {
                if (res.success) location.reload();
                else alert(res.message);
            });
        }

        // OPEN EDIT POPUP
        function editClass(id) {
            $.get(""https://localhost:44382/Master/GetCourse?id="" + id, function (data) {
                $(""#editId"").val(data.id);
                $(""#editName"").val(data.name);
                $(""#editModal"").show();
            });
        }

        // UPDATE
