        function saveClass() {

           

            var name = $(""#txtName"").val();
            var className = $('#ddlClass option:selected').text();
            var courseName = $('#ddlSubject option:selected').text();

            $.post(BASE_URL + ""/Suject/CreateSubject"", {
                Name: name,
                SelectedClass: className,
                SelectedCourse: courseName
            }, function (res) {
                $('#modal-default').modal('hide');
                location.reload();
                Swal.fire({
                    icon: 'success',
                    title: 'Success!',
                    text: 'Subject saved successfully!'
                });
            });
        }

